import React, { useState } from 'react';
import { View, Text, StyleSheet, ScrollView, TextInput, Button } from 'react-native';
import Cores from '../constantes/Cores';
import { useDispatch } from 'react-redux'

import * as contatosActions from '../store/contatos-actions';
import TiraFoto from '../componentes/TiraFoto';


const NovoContatoTela = (props) => {

    const [nome, setNome] = useState('');

    const [telefone, setTelefone] = useState();

    // const fotoTirada = imagemURI => {
    //     setImagemURI(imagemURI);
    // }

    const dispatch = useDispatch();

    const adicionarContato = () => {
        dispatch(contatosActions.addContato(nome, telefone));
        props.navigation.goBack();
    }


    return (
        <ScrollView>
            <View style={estilos.form}>
                <Text style={estilos.titulo}>Novo Contato</Text>
                <TextInput
                    style={estilos.textInput}
                    onChangeText={(texto) => setNovoContato(texto)}
                    value={nome} />
                {/* <TiraFoto onFotoTirada={fotoTirada} /> */}
                <Button
                    title="Salvar contato"
                    color={Cores.primary}
                    onPress={adicionarContato}
                />
            </View>
        </ScrollView>
    );
}

const estilos = StyleSheet.create({
    textInput: {
        borderBottomColor: '#DDD',
        borderBottomWidth: 2,
        marginBottom: 15,
        paddingVertical: 4
    },
    titulo: {
        fontSize: 18,
        marginBottom: 15
    },
    form: {
        margin: 30
    }
});

export default NovoContatoTela;
